arguments.argument:
yh=0
repeat (60)
{
xh=0
repeat (80)
{
if distance_to_point(view_left[0]+xh*8,view_top[0]+yh*8) > 159
{
draw_sprite(sprite4,0,view_left[0]+xh*8,view_top[0]+yh*8)
}
else
{
if distance_to_point(view_left[0]+xh*8,view_top[0]+yh*8) > 127
{
draw_sprite(sprite1,0,view_left[0]+xh*8,view_top[0]+yh*8)
}
else
{
if distance_to_point(view_left[0]+xh*8,view_top[0]+yh*8) > 95
{
draw_sprite(sprite2,0,view_left[0]+xh*8,view_top[0]+yh*8)
}
else
if distance_to_point(view_left[0]+xh*8,view_top[0]+yh*8) > 63
{
draw_sprite(sprite3,0,view_left[0]+xh*8,view_top[0]+yh*8)
}
}
}
xh+=1
}
yh+=1
}
